var request = require('request');
module.exports = {

  send: function(msg, token) {
    request({
      method: 'post',
      uri: 'https://notify-api.line.me/api/notify',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      'auth': {
        'bearer': token
      },
      form: {
        message: msg,

      }
    }, (err, httpResponse, body) => {
      console.log(err);
    });
  }
};